INSERT INTO `static_pages_versions` VALUES ('2', '', '', '', '<div class=\"SETSEEDcomponent SETSEEDcomponentZones ComponentColumnsZones-Content_With_Sidebar.tpl\"><div><div><div class=\"componentZonesTitle\"><a class=\"componentDelete\"></a>Content With Sidebar</div><div class=\"zonesDividers clearfix\"><div class=\"customZoneTitle\" id=\"main_content\">main content</div><div class=\"bpe_image\"><img src=\"/images/devices-elegant.png?\" / /></div><div class=\"customZoneTitle\" id=\"sidebar_content\">sidebar content</div><h4 style=\"text-align:left\">Elegant</h4><p style=\"text-align:left\">A simple, spacious theme with integrity and wisdom.</p><p style=\"text-align:left\" class=\"Button_Large\"><a href=\"/my-sites/?addsite=true&amp;theme=global_elegant&amp;theme_text=Elegant\">Start new website with Elegant</a></p><div class=\"zonesEnd\"></div></div></div></div></div><div class=\"SETSEEDcomponent ComponentCustom-Horizontal_Rule.tpl\"><div><div><div><a class=\"componentDelete\"></a><strong>Widget:</strong> Horizontal Rule</div></div></div></div><div class=\"bpe_split_divider Header\" alt=\"Header\">Header</div><div class=\"SETSEEDcomponent SETSEEDcomponentZones ComponentColumnsZones-Individual_Centered_Wide.tpl\"><div><div><div class=\"componentZonesTitle\"><a class=\"componentDelete\"></a>Individual Centered Wide</div><div class=\"zonesDividers clearfix\"><div class=\"customZoneTitle\" id=\"content\">content</div><h1 style=\"text-align:center\">Choose your theme and start your free website today</h1><p style=\"text-align:center\">No card details required. No time limits. Your free website lets you create 3 pages. Buy unlimited pages and loads of awesome features in the Features Store.</p><div class=\"zonesEnd\"></div></div></div></div></div><div class=\"bpe_split_divider Background_Section_Image\" alt=\"Background_Section_Image\">Background Section Image</div><div class=\"bpe_split_divider Background_Section_Content\" alt=\"Background_Section_Content\">Background Section Content</div><div class=\"bpe_split_divider Below_Background_Section\" alt=\"Below_Background_Section\">Below Background Section</div><div class=\"bpe_split_divider Above_Footer\" alt=\"Above_Footer\">Above Footer</div><div class=\"bpe_split_divider Footer\" alt=\"Footer\">Footer</div>\n', '', '', '1431087189', '251', '', '', '', '', '1', '');
