INSERT INTO `static_pages_versions` VALUES ('17', '', '', '', '<div class=\"SETSEEDcomponent SETSEEDcomponentZones ComponentCustomZones-Content_Blueprint.tpl\"><div><div><div class=\"componentZonesTitle\"><a class=\"componentDelete\"></a>Content Blueprint</div><div class=\"zonesDividers clearfix\"><div class=\"customZoneTitle\" id=\"blueprint_text\">blueprint text</div><p style=\"text-align:left\"><strong>Content blueprint</strong><br>A streamlined drag-and-drop interface just for working with content. Layout and styling isnt shown in the blueprint so your clients can focus on their content.</p><div class=\"customZoneTitle\" id=\"preview_text\">preview text</div><p style=\"text-align:left\"><strong>Live preview of changes</strong><br>An exact preview of the current draft. No fiddly edit-in-place controls shoehorned onto the site like other CMSs.</p><div class=\"zonesEnd\"></div></div></div></div></div>', '', '', '1433432591', '254', '', '', '', '', '1', '');
