INSERT INTO `static_pages_versions` VALUES ('19', '', '', '', '<h2 style=\"text-align:center\">Meet a new way to edit your content</h2><div class=\"SETSEEDcomponent SETSEEDcomponentZones ComponentColumnsZones-Content_With_Sidebar.tpl\"><div><div><div class=\"componentZonesTitle\"><a class=\"componentDelete\"></a>Content With Sidebar</div><div class=\"zonesDividers clearfix\"><div class=\"customZoneTitle\" id=\"main_content\">main content</div><div class=\"SETSEEDcomponent ComponentSnippet-254\"><div><div><div><a class=\"componentDelete\"></a><strong>Snippet:</strong> Blueprint</div></div></div></div><div class=\"customZoneTitle\" id=\"sidebar_content\">sidebar content</div><p style=\"text-align:left\">Work with your content in a super-clean single list view, without styles and layout getting in the way.</p><p style=\"text-align:left\">See changes in real-time with the side-by-side preview and enjoy a robust, fast and fun system that radically simplifies the process of working with website content.</p><p style=\"text-align:left\" class=\"Button_Large\"><a href=\"/choose-your-theme/\">Start Free Website Now</a></p><div class=\"zonesEnd\"></div></div></div></div></div><div class=\"bpe_split_divider Header\" alt=\"Header\">Header</div><div class=\"SETSEEDcomponent SETSEEDcomponentZones ComponentColumnsZones-Individual_Centered_Wide.tpl\"><div><div><div class=\"componentZonesTitle\"><a class=\"componentDelete\"></a>Individual Centered Wide</div><div class=\"zonesDividers clearfix\"><div class=\"customZoneTitle\" id=\"content\">content</div><h1 style=\"text-align:center\">Start your free content-driven website today</h1><p style=\"text-align:center\" class=\"Button_Large\"><a href=\"/choose-your-theme/\">Start Free Website Now</a></p><div class=\"zonesEnd\"></div></div></div></div></div><div class=\"bpe_split_divider Background_Section_Image\" alt=\"Background_Section_Image\">Background Section Image</div><div class=\"bpe_image\"><img src=\"/images/cms-screenshot-desktop.